    kbd,
    code {
        font-family: var(--mono-combo), ui-monospace, SFMono-Regular, SF Mono,
            Menlo, Consolas, Liberation Mono, monospace !important;
    }

    .blob-code-inner,
    .blob-num,
    pre {
        font-size: 16px !important;
    }
}

