/* ==UserStyle==
@name           monospace
@namespace      github.com/openstyles/stylus
@version        2.11.0
@description    My personal changes to monospace fonts, etc
@author         Zerohazard8x LLM
@updateURL      https://github.com/Zerohazard8x/custom/raw/main/stylus/monospace.user.css
@homepageURL    https://github.com/Zerohazard8x/custom/tree/main/stylus
==/UserStyle== */

@-moz-document domain("github.com"), domain("wikipedia.org"), domain("reddit.com"), domain("stackoverflow.com"), domain("superuser.com"), domain("stackexchange.com"), domain("chat.openai.com"), domain("w3schools.com"), domain("tcrf.net"), domain("mariowiki.com"), domain("ssbwiki.com"), domain("wikirby.com"), domain("splatoonwiki.org"), domain("niwanetwork.org"), domain("betawiki.net"), domain("raymanpc.com"), domain("consolemods.org"), domain("wiktionary.org"), domain("bing.com"), domain("copilot.microsoft.com"), domain("geeksforgeeks.org"), domain("discord.com"), domain("duckduckgo.com"), domain("remywiki.com"), domain("cagematch.net"), domain("perplexity.ai"), domain("instructure.com"), domain("minecraft.wiki"), domain("pcgamingwiki.com"), domain("liquipedia.net"), domain("redlib.privacyredirect.com"), domain("redlib.catsarch.com"), domain("redlib.private.coffee"), domain("undelete.pullpush.io"), domain("deskthority.net"), domain("libreddit.bus-hit.me"), domain("redlib.freedit.eu"), regexp("^.*www\.google\.com.*"), regexp("^(?!.*(\w+\.google\.com)).*google\.com.*") {
    :root {
        /* Define variables */
        --noto: var(--emoji, ""), "Noto Sans JP", "Noto Sans TC", "Noto Sans SC",
            "Noto Sans KR", "Noto Sans Thai", "Noto Sans Devanagari", PingFang,
            "PingFang SC", "Hiragino Sans", "Apple SD Gothic Neo", Thonburi,
            "Tiro Devanagari Hindi", "Geeza Pro", Geeza, "Yu Gothic",
            "Microsoft YaHei UI", "Microsoft YaHei", "Malgun Gothic",
            "Leelawadee UI", "Nirmala UI", "Segoe UI Arabic";
        /* --noto-full: "Noto Sans JP", "Noto Sans TC", "Noto Sans SC",
            "Noto Sans KR", "Noto Sans Thai", "Noto Sans Devanagari",
            "Noto Sans Arabic", "Noto Sans", "FOT-Rodin Pro",
            "A-OTF UD Shin Go Pro", "FOT-NewRodin Pro", "Hiragino Sans",
            "Hiragino Sans GB", "Hiragino Kaku Gothic",
            "Hiragino Kaku Gothic Pro", "HarmonyOS Sans TC", "HarmonyOS Sans SC",
            "HarmonyOS Sans", PingFang, "PingFang SC", "Hiragino Sans GB",
            "Apple SD Gothic Neo", Thonburi, "Tiro Devanagari Hindi",
            "Geeza Pro", Geeza, "Yu Gothic", "Microsoft YaHei UI",
            "Microsoft YaHei", "Malgun Gothic", "Leelawadee UI", "Nirmala UI",
            "Segoe UI Arabic", var(--emoji, ""); */
        --emoji: "Apple Color Emoji", "SF Symbols", "Segoe UI Emoji",
            "Segoe Fluent Icons", "Segoe MDL2 Assets", "Segoe UI Symbol",
            "Noto Color Emoji", "Noto Sans Symbols", "EmojiOne Color",
            "Twemoji Mozilla";
        --sans: -apple-system, BlinkMacSystemFont, "Inter V", "Roboto",
            "Avenir Next", Avenir, "Segoe UI Variable", "Segoe UI",
            var(--noto, "");
        /* --sans-full: -apple-system, BlinkMacSystemFont, "SF Pro", "Roboto Flex",
            "Roboto", Barlow, Whitney, Lato, "Segoe UI Variable", "Segoe UI",
            "Motiva Sans", "Proxima Vara", "Proxima Nova", Inter, "Avenir Next",
            Avenir, "Nunito", "Nunito Sans", "Fira Sans", FiraGO, "FF Meta",
            Manrope, "Helvetica Neue", "Helvetica", "IBM Plex Sans JP",
            "IBM Plex Sans KR", "IBM Plex Sans", "Ubuntu", var(--noto, ""),
            "Open Sans", "Droid Sans"; */
        --serif: Rockwell, Century, "Century Schoolbook", Georgia,
            "Noto Serif JP", "Noto Serif TC", "Noto Serif SC", "Noto Serif KR",
            "Noto Serif Thai", "Noto Serif Devanagari", "Noto Naskh Arabic",
            "Noto Serif", var(--noto, "");
        /* --serif-full: "Source Serif", "Source Serif Pro", "Source Serif 4",
            "STIX Two Text", "Beaufort", "Beaufort Pro", Rockwell, Rokkitt,
            "Zilla Slab", "Serif UI", "Serif UI Text", Fraunces, Alegreya,
            Crimson, "Crimson Pro", "Crimson Text", Lora, Century,
            "Century Schoolbook", "Libre Baskerville", Georgia, "ITC Garamond",
            "Apple Garamond", "Shippori Mincho", "Serif UI Display",
            "Noto Serif JP", "Noto Serif TC", "Noto Serif SC", "Noto Serif KR",
            "Noto Serif Thai", "Noto Serif Devanagari", "Noto Serif",
            var(--noto, ""); */
        --monospace: "Monaspace Argon Var", "Monaspace Argon VF",
            "Monaspace Argon", "Fira Mono";
        /* --monospace-full: "Monaspace Argon Var", "Monaspace Argon VF",
            "Monaspace Argon", "Fira Code VF", "Fira Code", "Fira Mono",
            "Fira Code Retina", "Noto Mono", "Noto Sans Mono", "DroidSansM",
            "SF Mono", "SFMono-Regular", "CodeNewRoman Nerd Font", Consolas,
            Hack, "Hack Nerd Font", "Bitstream Vera Sans Mono",
            "BitstromWera Nerd Font", "DejaVu Sans Mono", "Cascadia Code",
            "CaskaydiaCove Nerd Font", "Cascadia Mono", "Segoe UI Mono",
            "Intel One Mono", Menlo, "MesloLG Nerd Font", Monaco, "Ubuntu Mono",
            "Ubuntu Mono Nerd Font", Agave, "Agave Nerd Font", Hasklig,
            "Hasklug Nerd Font", "Source Code Pro", "Source Code Pro Nerd Font",
            "Andale Mono", "Lucida Console", "Lucida Sans Typewriter"; */
        --block-mono: "JetBrains Mono", "JetBrains Mono NL",
            "JetBrainsMono Nerd Font", "Fantasque Sans Mono";
        /* --block-mono-full: "JetBrains Mono", "JetBrains Mono NL",
            "JetBrainsMono Nerd Font", "Fantasque Sans Mono",
            "FantasqueSansM Nerd Font", "Input Mono", "Envy Code R",
            "EnvyCodeR Nerd Font", "Monaspace Neon Var", "Monaspace Neon VF",
            "Monaspace Neon", "IBM Plex Mono", "BlexMono Nerd Font",
            "Roboto Mono", ProFont, "Monaspace Krypton Var",
            "Monaspace Krypton VF", "Monaspace Krypton", ProFontIIx,
            ProFontWindows, "Iosevka", "Iosevka Nerd Font",
            "IosevkaTerm Nerd Font", "Input Sans"; */
        /* --mono-combo: var(--monospace, ""); */
        --mono-combo: var(--block-mono, ""), var(--monospace, "");
    }
}

