    .l_ecrd_ftr_txt,
    .rd_gtrtb_nav_item,
    .rstc,
    .sto_attr_header,
    .fdbtext,
    .b_fbText,
    .b_seeMoreCont,
    .b_algospacing,
    .recommendationsTableTitle > h2 {
        font-family: var(--footer-small), Arial, sans-serif !important;
        filter: opacity(100%) !important;
    }

    .sa_rm {
        font-family: var(--footer-small), "Arial", "Helvetica", "sans-serif" !important;
