    --monospace: "Hack", "Bitstream Vera Sans Mono", "BitstromVera",
        "DejaVu Sans Mono", "Cascadia Code", "Cascadia Mono", "CaskaydiaCove",
        "Source Code Pro", "Segoe UI Mono", "Fira Code", "Fira Mono",
        "Noto Mono", "DroidSansM", "SF Mono", "SFMono-Regular", Menlo, Monaco,
        "Consolas", "Andale Mono", "Lucida Console", "Lucida Sans Typewriter";
    --block-mono: "JetBrains Mono", "JetBrains Mono NL", "Input Mono",
        "IBM Plex Mono", "BlexMono", "Roboto Mono", "Iosevka Expanded",
        "Iosevka", "Input Sans", "Liberation Mono";
}

