/* ==UserStyle==
@name           Messenger Dark
@description    A dark theme for Facebook Messenger.
@author         reizumi
@namespace      codeberg.org/reizumi/MessengerDark
@homepageURL    https://codeberg.org/reizumi/MessengerDark
@updateURL      https://codeberg.org/reizumi/MessengerDark/raw/branch/master/messenger-dark.user.css
@supportURL     https://codeberg.org/reizumi/MessengerDark/issues
@preprocessor   stylus
@version        1.3.1
@license        CC-BY-SA-4.0

@var select     theme           "Preset color theme" {
    "System default"    : "system",
    "Dark*"             : "dark",
    "Darker"            : "darker",
    "Gruvbox"           : "gruvbox",
    "Nord"              : "nord",
    "Discord"           : "discord",
    "Mastodon"          : "mastodon",
    "Moonlight"         : "moonlight",
    "Proton"            : "proton",
    "Breeze"            : "breeze",
    "Adwaita"           : "adwaita",
    "Fuchsia"           : "fuchsia-dark",
    "Custom"            : "custom",
}
@var text       fontName        "Custom font"                   default
@var checkbox   colorScrollbars "Theme scrollbars"              1
@var checkbox   colorConvos     "Theme conversation colors"     0
@var checkbox   colorSelect     "Theme text selection"          0
@var color      bg-mt           "Message search match color"    #f7b928
@var color      bg-ac           "Selected chat color"           rgba(255, 255, 255, .06)
@var color      bg-1            "Custom background 1"           #222
@var color      bg-2            "Custom background 2"           #2b2b2b
@var color      bg-3            "Custom background 3"           #333
@var color      fg-1            "Custom foreground 1"           #fff
@var color      fg-2            "Custom foreground 2"           #aaa
@var color      ic-1            "Custom icon color"             #aaa
@var color      ac-1            "Custom accent color"           #09f
@var color      bg-bb           "Custom sender bubble color"    #07b
@var text       ic-2            "Custom icon filter"            invert(79%) sepia(2%) saturate(19%) hue-rotate(325deg) brightness(89%) contrast(81%)
@var text       ac-2            "Custom accent filter"          invert(50%) sepia(94%) saturate(3979%) hue-rotate(183deg) brightness(104%) contrast(103%)
@var checkbox   darkButtonText  "Custom: Dark button text"      0
==/UserStyle== */

//  Some icons require CSS filters due to them using images instead of vectors. (eg. three-dot menu, arrows, and payment icons)
//  To make or generate your own CSS filter, you can use any tool that converts color values to CSS filter.
//  Convert ic-1 (icon) and ac-1 (accent color) color values for consistency, or do whatever you want.

@-moz-document domain("messenger.com") {
    // Preset color themes
    if theme == dark || theme == system {
        bg-1 = #222;
        bg-2 = #2b2b2b;
        bg-3 = #333;
        fg-1 = #fff;
        fg-2 = #aaa;
        ic-1 = #aaa;
        ac-1 = #09f;
        bg-bb = #07b;
        ic-2 = invert(79%) sepia(2%) saturate(19%) hue-rotate(325deg) brightness(89%) contrast(81%);
        ac-2 = invert(50%) sepia(94%) saturate(3979%) hue-rotate(183deg) brightness(104%) contrast(103%);
    } else if theme == darker {
        bg-1 = #111;
        bg-2 = #1b1b1b;
        bg-3 = #222;
        fg-1 = #eee;
        fg-2 = #999;
        ic-1 = #999;
        ac-1 = #06c;
        bg-bb = #048;
        ic-2 = invert(72%) sepia(0%) saturate(6%) hue-rotate(251deg) brightness(86%) contrast(82%);
        ac-2 = invert(24%) sepia(95%) saturate(1745%) hue-rotate(195deg) brightness(96%) contrast(104%);
    } else if theme == gruvbox {
        bg-1 = #282828;
        bg-2 = #3c3836;
        bg-3 = #32302f;
        fg-1 = #ebdbb2;
        fg-2 = #a89984;
        ic-1 = #a89984;
        ac-1 = #83a598;
        bg-bb = #076678;
        ic-2 = invert(67%) sepia(5%) saturate(1223%) hue-rotate(356deg) brightness(91%) contrast(91%);
        ac-2 = invert(77%) sepia(3%) saturate(1992%) hue-rotate(105deg) brightness(82%) contrast(87%);
    } else if theme == nord {
        bg-1 = #2e3440;
        bg-2 = #364054;
        bg-3 = #424c60;
        fg-1 = #eceff4;
        fg-2 = #d8dee9;
        ic-1 = #d8dee9;
        ac-1 = #7ea1cc;
        bg-bb = #587ba6;
        ic-2 = invert(95%) sepia(6%) saturate(837%) hue-rotate(185deg) brightness(95%) contrast(91%);
        ac-2 = invert(66%) sepia(24%) saturate(566%) hue-rotate(173deg) brightness(91%) contrast(87%);
    } else if theme == discord {
        bg-1 = #36393f;
        bg-2 = #3d414a;
        bg-3 = #4d515a;
        fg-1 = #fff;
        fg-2 = #99aab5;
        ic-1 = #99aab5;
        ac-1 = #7289da;
        bg-bb = #5c73c5;
        ic-2 = invert(74%) sepia(8%) saturate(537%) hue-rotate(160deg) brightness(90%) contrast(84%);
        ac-2 = invert(58%) sepia(26%) saturate(1117%) hue-rotate(192deg) brightness(90%) contrast(89%);
    } else if theme == mastodon {
        bg-1 = #191b22;
        bg-2 = #272b36;
        bg-3 = #313543;
        fg-1 = #fff;
        fg-2 = #9baec8;
        ic-1 = #9baec8;
        ac-1 = #8c8dff;
        bg-bb = #4d4dee;
        ic-2 = invert(73%) sepia(5%) saturate(1409%) hue-rotate(176deg) brightness(96%) contrast(83%);
        ac-2 = invert(51%) sepia(27%) saturate(1019%) hue-rotate(201deg) brightness(108%) contrast(106%);
    } else if theme == moonlight {
        bg-1 = #223;
        bg-2 = #2b2b3c;
        bg-3 = #334;
        fg-1 = #fff;
        fg-2 = #aab;
        ic-1 = #aab;
        ac-1 = #99e;
        bg-bb = #66b;
        ic-2 = invert(77%) sepia(18%) saturate(187%) hue-rotate(201deg) brightness(88%) contrast(86%);
        ac-2 = invert(62%) sepia(28%) saturate(665%) hue-rotate(202deg) brightness(97%) contrast(93%);
    } else if theme == proton {
        bg-1 = #1c1b22;
        bg-2 = #23222b;
        bg-3 = #2b2a33;
        fg-1 = #fff;
        fg-2 = #aaa;
        ic-1 = #fbfbfe;
        ac-1 = #0df;
        bg-bb = #126171;
        ic-2 = invert(97%) sepia(8%) saturate(1873%) hue-rotate(183deg) brightness(107%) contrast(99%);
        ac-2 = invert(65%) sepia(32%) saturate(2926%) hue-rotate(145deg) brightness(104%) contrast(105%);
    } else if theme == breeze {
        bg-1 = #232629;
        bg-2 = #292d32;
        bg-3 = #31363b;
        fg-1 = #fff;
        fg-2 = #aaa;
        ic-1 = #aaa;
        ac-1 = #3daee9;
        bg-bb = #224e65;
        ic-2 = invert(79%) sepia(2%) saturate(19%) hue-rotate(325deg) brightness(89%) contrast(81%);
        ac-2 = invert(63%) sepia(20%) saturate(1601%) hue-rotate(164deg) brightness(95%) contrast(93%);
    } else if theme == adwaita {
        bg-1 = #242424;
        bg-2 = #2b2b2b;
        bg-3 = #353535;
        fg-1 = #f5f5f5;
        fg-2 = #aaa;
        ic-1 = #eeeeec;
        ac-1 = #78aeed;
        bg-bb = #1b70c9;
        ic-2 = invert(97%) sepia(8%) saturate(607%) hue-rotate(352deg) brightness(115%) contrast(87%);
        ac-2 = invert(63%) sepia(19%) saturate(1049%) hue-rotate(174deg) brightness(103%) contrast(86%);
    } else if theme == fuchsia-dark {
        bg-1 = #342434;
        bg-2 = #3b2b3b;
        bg-3 = #463546;
        fg-1 = #fdf;
        fg-2 = #a8a;
        ic-1 = #cbc;
        ac-1 = #c74375;
        bg-bb = #7b4b7b;
        ic-2 = invert(98%) sepia(86%) saturate(359%) hue-rotate(262deg) brightness(81%) contrast(96%);
        ac-2 = invert(45%) sepia(24%) saturate(7496%) hue-rotate(312deg) brightness(83%) contrast(85%);
    }
    // Dark button text and icons
    if theme == custom && darkButtonText {
        ic-bn = invert(0%);
        fg-bn = bg-1;
    }
    else {
        ic-bn = invert(100%);
        fg-bn = fg-1;

        if light(ac-1) && theme != custom {
            ic-bn = invert(0%);
            fg-bn = bg-1;
        }
    }
    // Default: Custom scrollbars
    if colorScrollbars {
        // Firefox
        html,
        div {
            scrollbar-width: thin;
        }
        // Chromium-based browsers
        ::-webkit-scrollbar {
            background-color: #fff;
            width: 14px;

            &-thumb {
                background-color: rgba(#808080, .25);
                background-clip: padding-box;
                border: 3px solid transparent;
                border-radius: 999px;
            }
        }
    }
    /* Function: Dark mode */
    dark_mode() {
        // Root variables
        :root,
        .__fb-light-mode,
        .__fb-dark-mode {
            --messenger-card-background: bg-1 !important;
            --surface-background: bg-1 !important;
            --card-background: bg-1 !important;
            --card-background-flat: bg-3 !important;
            --popover-background: bg-1 !important;
            --messenger-reply-background: bg-2 !important;
            --secondary-button-background: bg-3 !important;
            --secondary-button-background-floating: bg-2 !important;
            --disabled-button-background: bg-2 !important;
            --wash: bg-3 !important;
            --web-wash: bg-1 !important;
            --comment-footer-background: bg-2 !important;
            --background-deemphasized: bg-2 !important;

            --primary-button-background: ac-1 !important;
            --primary-deemphasized-button-text: ac-1 !important;
            --accent: ac-1 !important;
            --blue-link: ac-1 !important;
            --base-blue: ac-1 !important;
            --base-lemon: bg-mt !important;
            --switch-active: ac-1 !important;

            --primary-text: fg-1 !important;
            --secondary-text: fg-2 !important;
            --placeholder-text: fg-2 !important;
            --disabled-text: fg-2 !important;
            --disabled-button-text: fg-2 !important;
            --primary-button-text: fg-bn !important;
            --secondary-button-text: fg-1 !important;
            --primary-text-on-media: fg-1 !important;

            --hosted-view-selected-state: bg-ac !important;
            --primary-deemphasized-button-background: rgba(ac-1, .08) !important;
            --primary-deemphasized-button-pressed-overlay: rgba(fg-1, .04) !important;
            --comment-background: rgba(fg-2, .1) !important;
            --hover-overlay: rgba(fg-1, .05) !important;
            --press-overlay: rgba(fg-1, .05) !important;
            --overlay-alpha-80: rgba(bg-1, .80) !important;
            --media-inner-border: rgba(fg-1, .1) !important;
            --shadow-inset: rgba(bg-3, 0.5) !important;
            --progress-ring-neutral-foreground: rgba(fg-1, .4) !important;
            --scroll-thumb: rgba(fg-1, .25) !important;
            --divider: rgba(fg-1, .1) !important;
            --always-black: bg-1 !important;
            --always-white: fg-1 !important;

            --primary-icon: ic-1 !important;
            --secondary-icon: ic-1 !important;
            --placeholder-icon: ic-1 !important;
            --disabled-icon: ic-1 !important;
            --filter-primary-icon: ic-2 !important;
            --filter-secondary-icon: ic-2 !important;
            --filter-disabled-icon: ic-2 !important;
            --filter-placeholder-icon: ic-2 !important;
            --filter-accent: ac-2 !important;

            --fds-gray-00: lighten(bg-1, 0%) !important;
            --fds-gray-05: lighten(bg-1, 5%) !important;
            --fds-gray-10: lighten(bg-1, 10%) !important;
            --fds-gray-20: lighten(bg-1, 20%) !important;
            --fds-gray-25: lighten(bg-1, 25%) !important;
            --fds-gray-30: lighten(bg-1, 30%) !important;
            --fds-gray-45: lighten(bg-1, 45%) !important;
            --fds-gray-70: lighten(bg-1, 70%) !important;
            --fds-gray-80: lighten(bg-1, 80%) !important;
            --fds-gray-90: lighten(bg-1, 90%) !important;
            --fds-gray-100: lighten(bg-1, 100%) !important;

            // Color scheme support
            @supports (color-scheme: dark) {
                color-scheme: dark;
            }
        }
        // Dark: Custom scrollbars
        if colorScrollbars {
            // Firefox
            html,
            div {
                scrollbar-color: rgba(fg-1, .25) bg-1;
            }
            // Chromium-based browsers
            ::-webkit-scrollbar {
                background-color: bg-1;

                &:hover {
                    background-color: bg-2;
                }
                &-thumb {
                    background-color: rgba(fg-1, .25);
                }
            }
        }
        // Theme Messenger colors
        if colorConvos {
            // Chat bubbles
            .x1tlxs6b.x1g8br2z.x14ctfv.x6ikm8r.x10wlt62.x1mzt3pk.x1y1aw1k.xn6708d.xwib8y2.x1ye3gou.x1n2onr6.x13faqbe.x1vjfegm.x5pf9jr.xo71vjh.x9f619.x16it46q.xh8yej3:not([style*="var(--"]),
            .x1tlxs6b.x14ctfv.x6ikm8r.x10wlt62.x1mzt3pk.x1y1aw1k.xn6708d.xwib8y2.x1ye3gou.x1n2onr6.x13faqbe.x1vjfegm.x1yr5g0i.x5pf9jr.xo71vjh.x9f619.x16it46q.xh8yej3:not([style*="var(--"]),
            .x1tlxs6b.x1g8br2z.x1gn5b1j.x230xth.x14ctfv.x1okitfd.x6ikm8r.x10wlt62.x1mzt3pk.x1y1aw1k.xn6708d.xwib8y2.x1ye3gou.x1n2onr6.x13faqbe.x1vjfegm:not([style*="var(--"]),
            .x1tlxs6b.x1gn5b1j.x230xth.x14ctfv.x1okitfd.x6ikm8r.x10wlt62.x1mzt3pk.x1y1aw1k.xn6708d.xwib8y2.x1ye3gou.x1n2onr6.x13faqbe.x1vjfegm.x1yr5g0i:not([style*="var(--"]),
            .x1tlxs6b.x230xth.x14ctfv.x1okitfd.x6ikm8r.x10wlt62.x1mzt3pk.x1y1aw1k.xn6708d.xwib8y2.x1ye3gou.x1n2onr6.x13faqbe.x1vjfegm.x1yr5g0i.xrt01vj:not([style*="var(--"]),
            .x1tlxs6b.x1g8br2z.x230xth.x14ctfv.x1okitfd.x6ikm8r.x10wlt62.x1mzt3pk.x1y1aw1k.xn6708d.xwib8y2.x1ye3gou.x1n2onr6.x13faqbe.x1vjfegm.xrt01vj:not([style*="var(--"]),
            .x6s0dn4.x78zum5.x1iyjqo2.x1qughib.x6ikm8r.x10wlt62.x1n2onr6:not([style*="var(--"]) {
                background-color: bg-bb !important;
            }
            // Icon colors
            svg[fill]:not([fill="var(--card-background)"]),
            path[fill],
            rect[fill]:not(.qbubdy2e svg rect):not([fill="none"]):not(.x1pwv2dq.xvlca1e.xbh8q5q) {
                fill: ac-1 !important;
            }
            // Menu options
            .x1dmfqyo g {
                stroke: ac-1 !important;
            }
            // Page options
            .x1pju0fl[role="button"] {
                background-color: ac-1 !important;

                svg path {
                    fill: fg-bn !important;
                }
            }
            // Audio message timestamp
            .x6s0dn4.x14hiurz.xfj52jq.xykzs3s.x17xv1ja.x1mvicbb.x1mqxbix.x78zum5.x1nxh6w3.x1s688f.xi0exxh.xl56j7k.x1w0mnb.x1mnrxsn.x1n2onr6.x87ps6o.x1r9kitl:not([style*="var(--"]) {
                color: bg-bb !important;
            }
            // Chat bubble text color
            .x1yc453h.x126k92a.x6prxxf.x1fc57z9.x14ctfv {
                color: fg-1 !important;
            }
        } else {
            .x1yc453h.x126k92a.x6prxxf.x1fc57z9.x14ctfv {
                color: #fff !important;
            }
        }
        // Theme text selection
        if colorSelect {
            ::selection {
                background-color: rgba(ac-1, .4);
            }
        }
        // Icon colors
        svg[fill="currentColor"]:not(.x1fey0fg),
        path[fill*="var(--"],
        path[fill="black"] {
            fill: ic-1 !important;
        }
        .xtvsq51 .xaj1gnb {
            filter: ic-bn;
        }
        // Close search button
        .x14hiurz.__fb-light-mode.x1r1pt67 {
            background-color: bg-2 !important;
        }
        // Video progress and volume bars
        .x1evw4sf {
            background-color: ac-1;
        }
        // Hidden images
        .x11dxs5c {
            background-color: bg-2;
        }
        // File attachments
        .x1ybostu {
            background-color: bg-2;
        }
        // Message buttons
        .x1qhmfi1 {
            background-color: rgba(fg-1, .1);
        }
        // Focus rings
        .x90kdol {
            --always-white: bg-1;
        }
        .xqnlwer {
            --placeholder-text: lighten(bg-3, 15%);
        }
        // Document preview
        .x1yi8jrw {
            background-color: bg-1;

            .x1lliihq.x1k90msu.x2h7rmj.x1qfuztq.x14ctfv {
                fill: fg-bn !important;
            }
        }
        // Loading spinner
        path[style="stroke: rgb(24\, 119\, 242);"] {
            stroke: ac-1 !important;
            fill: none !important;
        }
        rect[style="stroke: rgb(204\, 208\, 213);"] {
            stroke: bg-2 !important;
        }
        // Conversation loading bar
        .x1xzmf5g {
            background-color: bg-2;
        }
        .x4o00kh {
            background-color: ac-1;
        }
    }
    // Custom interface font
    if fontName != default {
        body {
            font-family: fontName, system-ui, sans-serif !important;
        }
    }
    // Apply dark mode based on system preference
    if theme == system {
        @media (prefers-color-scheme: dark) {
            dark_mode();
        }
    }
    else {
        dark_mode();
    }
}

/* ==UserStyle==
