/* ==UserStyle==
@name           monospace
@namespace      github.com/openstyles/stylus
@version        3.0.0
@description    My personal changes to monospace fonts, etc
@author         Zerohazard8x LLM
@updateURL      https://github.com/Zerohazard8x/custom/raw/main/stylus/monospace.user.css
@homepageURL    https://github.com/Zerohazard8x/custom/tree/main/stylus
==/UserStyle== */
@-moz-document domain("github.com") {
    :root {
        --fontStack-monospace: __monaspaceNeon_4fcba3,
            __monaspaceNeon_Fallback_4fcba3, "Monaspace Neon Var",
            "Monaspace Neon VF", "Monaspace Neon", ui-monospace, SFMono-Regular,
            SF Mono, Menlo, Consolas, Liberation Mono, monospace,
            var(--foreign, "");
        --fontStack-sansSerif: "Mona Sans", "Mona Sans Header Fallback",
            -apple-system, BlinkMacSystemFont, "Segoe UI", "Noto Sans",
            Helvetica, Arial, "Apple Color Emoji", "Segoe UI Emoji",
            var(--foreign, "");
    }

    @layer primer-css-base {
        body {
            font-family: var(--fontStack-sansSerif, "") !important;
        }
    }

    .markdown-body {
        font-family: var(--fontStack-sansSerif, "") !important;
    }

    code,
    kbd,
    pre,
    samp {
        font-family: __monaspaceNeon_4fcba3, __monaspaceNeon_Fallback_4fcba3,
            "Monaspace Neon Var", "Monaspace Neon VF", "Monaspace Neon",
            monospace, var(--foreign, "") !important;
    }

    .react-blob-print-hide {
        font-family: __monaspaceNeon_4fcba3, __monaspaceNeon_Fallback_4fcba3,
            "Monaspace Neon Var", "Monaspace Neon VF", "Monaspace Neon",
            ui-monospace, SFMono-Regular, "SF Mono", Menlo, Consolas,
            "Liberation Mono", monospace, var(--foreign, "") !important;
    }
}

@-moz-document domain("wikipedia.org") {
    @media screen {
        html,
        body {
            font-family: var(--foreign, ""), sans-serif !important;
        }
    }

    body {
        font-family: -apple-system, "BlinkMacSystemFont", "Segoe UI", "Roboto",
            "Inter", "Helvetica", "Arial", var(--foreign, ""), sans-serif;
    }

    .mw-parser-output .script-arabic {
        font-family: -apple-system, "BlinkMacSystemFont", "Segoe UI", "Roboto",
            "Inter", "Helvetica", "Arial", var(--foreign, ""), sans-serif,
            "Scheherazade New", "SF Arabic", Amiri, "Noto Naskh Arabic",
            "Droid Arabic Naskh", "Noto Sans Arabic", "Sakkal Majalla",
            "Harmattan", "Arabic Typesetting", "Arabic Transparent",
            "Times New Roman", Arial, Calibri, "Microsoft Sans Serif",
            "Segoe UI", serif, sans-serif !important;
    }

    /* matches all with class containing heading */
    /* ... i] means case insensitive*/
    blockquote,
    .pre-content h1,
    .content h1,
    [class*="heading" i],
    .content h2 {
        font-family: "Linux Libertine", "Georgia", "Times", "Source Serif Pro",
            serif, var(--foreign-serif, "") !important;
    }

    @media screen {
        [class*="heading" i],
        .mw-body h1,
        .mw-body-content h1,
        .mw-body-content h2 {
            font-family: "Linux Libertine", "Georgia", "Times",
                "Source Serif Pro", serif, var(--foreign-serif, "") !important;
        }
    }

    .monospaced,
    .diff tbody tr * *,
    code,
    pre span {
        font-family: var(--mono, ""), monospace, var(--foreign, "") !important;
    }
}

