/* ==UserStyle==
@name           Zerohazard's Web Fonts
@namespace      github.com/openstyles/stylus
@version        3.4.1
@description    web font imports
@author         Zerohazard8x
@updateURL      https://github.com/Zerohazard8x/custom/raw/main/stylus/webfonts.user.css
@homepageURL    https://github.com/Zerohazard8x/custom/tree/main/stylus
==/UserStyle== */

@-moz-document regexp(".*") {
    @import url("https://fonts.googleapis.com/css?family=Roboto+Flex|Fira+Mono:ital,wght@0,100..900;1,100..900&display=swap");
