==/UserStyle== */
@-moz-document url-prefix("https://discord.com/") {
	:root {
		--background: #261b17;
		--header: #1b110e;
		--background-alt: #291812;
		--hover: #532f28;
		--highlight: #f99666;
		--highlight-alt: #fad256;
		--ui: #e35b15;
		--ui-alt: #fabc2c;
		--onclick: #e86420;
		--visited: #e04220;
		--text: #e4cbb3;
		--green-light: #82a23a;
		--green-dark: #4d772d;
		--blue-light: #66a4a4;
		--blue-dark: #4395a3;
		--purple-light: #c888e8;
		--purple-dark: #b666e6;
		--pink-light: #fab2c2;
		--pink-dark: #fff;
		--orange-light: #e86420;
		--orange-dark: #e35b15;
		--red-light: #e04220;
		--red-dark: #e01d1d;
		--brown-light: #291812;
		--brown-dark: #1b110e;
		--yellow-light: #fad256;
		--yellow-dark: #fabc2c;
		/* 
  .
 (
 )\
((_)       _    _   _ _____ _____ ___   _   _
 | |      / \  | \ | |_   _| ____|  _ \| \ | |
 | |     / _ \ |  \| | | | |  _| | |_) |  \| |
 | |___ / ___ \| |\  | | | | |___|  _ <| |\  |
 |_____/_/   \_|_| \_| |_| |_____|_| \_|_| \_|
  _____ _   _ _____ __  __ _____
 |_   _| | | | ____|  \/  | ____|
   | | | |_| |  _| | |\/| |  _|
   | | |  _  | |___| |  | | |___
   |_| |_| |_|_____|_|  |_|_____|
*/
	}
	/* Main colour variables, If something is pink, I don't know what it affects*/
	.theme-dark,
	.theme-light,
	:root {
		--background-primary: var(--background) !important;
		/* Main body colour */
		--background-secondary: var(--header) !important;
		--background-secondary-alt: var(--background-alt) !important;
		--background-tertiary: var(--header) !important;
		/* Side bar around servers*/
		--background-accent: var(--background-secondary) !important;
		--background-extra: var(--highlight) !important;
		--background-extra-alt: var(--hover) !important;
		--background-floating: var(--header) !important;
		--background-modifier-hover: var(--background-secondary-alt) !important;
		--background-modifier-active: var(--onclick) !important;
		/* colour of the channel when clicked */
		--background-modifier-selected: var(--background-alt) !important;
		--background-modifier-accent: var(--background) !important;
		/* Emote background colour  */
		--channeltextarea-background: var(--header) !important;
		--focus-primary: var(--ui) !important;
		/* Tabbing colour */
		--deprecated-card-bg: var(--header) !important;
		--deprecated-card-editable-bg: var(--background) !important;
		--deprecated-text-input-border-disabled: var(--header) !important;
		--activity-card-background: var(--background) !important;
		--channels-default: var(--highlight) !important;
		--text-muted: var(--highlight) !important;
		--text-normal: var(--text) !important;
		--text-link: var(--ui);
		/* link */
		--interactive-normal: var(--ui) !important;
		/* UI icons */
		--interactive-hover: var(--highlight) !important;
		--interactive-active: var(--text) !important;
		--interactive-muted: var(--ui) !important;
		/* Quotes */
		--header-secondary: var(--text) !important;
		--toast-background: var(--background) !important;
		--toast-header: var(--header) !important;
		--toast-contents: var(--header) !important;
		--toast-box-shadow: var(--background) !important;
		--toast-border: var(--header) !important;
		--background-mentioned-hover: rgba(227, 91, 21, 0.3) !important; /* Onhover */
		/*CHANGECOLOUR!*/
		--background-mentioned: rgba(227, 91, 21, 0.1);
		/* Just pong */
		/*CHANGECOLOUR!*/
		--header-primary: var(--highlight) !important;
		--border-radius: 18px;
		--deprecated-text-input-prefix: var(--background);
		--scrollbar-thin-thumb: var(--header);
		/* Channel and people sidebar baby scroll colour */
		--scrollbar-thin-track: transparent;
		--scrollbar-auto-thumb: var(--background);
		/* Message box baby scroll thingy */
		--scrollbar-auto-track: var(--background);
		/* Non scrolly part of the scroll */
		--scrollbar-auto-scrollbar-color-thumb: var(--background);
		--scrollbar-auto-scrollbar-color-track: var(--background);
		--elevation-low: transparent;
		/* imgur.com/a/PQuzp18 /line under channel description*/
		--elevation-high: 0 0px 0px var(--highlight);
		/* Glow around tooltips */
		--logo-primary: var(--background);
		--guild-header-text-shadow: 0 1px 1px var(--background);
		--deprecated-panel-background: var(--background);
		--deprecated-quickswitcher-input-background: var(--background-alt);
		/* CTRL+K background colour */
		--deprecated-quickswitcher-input-placeholder: var(--text);
		/* CTRL+K / channel search menu text colour */
		--deprecated-text-input-bg: var(--hover);
		/* Message box background person on profile card pop up*/
		--deprecated-text-input-border: var(--highlight);
		/* Profile cards message box border if not focus/hover*/
