		--search-popout-date-picker-hint-value-background-hover: var(--highlight-alt) !important;
		--brand-experiment: var(--ui) !important;
		--brand-experiment-hover: var(--ui-alt) !important;
		--button-danger-background: var(--visited) !important;
		--button-danger-background-hover: var(--ui) !important;
		--button-positive-background: var(--green-light) !important;
		--button-positive-background-hover: var(--green-dark) !important;
