		--control-brand-foreground: var(--ui) !important;
		--brand-experiment-560: var(--onclick) !important;
		--button-secondary-background: var(--ui) !important;
		--button-secondary-background-hover: var(--onclick) !important;
		--button-outline-danger-text: var(--text) !important;
		--button-outline-danger-border: var(--visited) !important;
		--button-outline-danger-background-hover: var(--visited) !important;
		--button-outline-danger-border-hover: var(--visited) !important;
		--radio-bar-accent-color: var(--green-light) !important;
		--brand-experiment-80a: var(--background-mentioned) !important;
		--brand-experiment-95a: var(--background-mentioned-hover) !important
	}
